package kyu5;

public class NumberOfTrailingZerosOfN {
    public static void main(String[] args) {
        System.out.println(zeros(10000));
    }

    public static int zeros(int n) {
     String a= "";
        System.out.println(a.length());
        int c = n/25;
        if (n>1000){
            c+=10;
        }
       return (n/5)+c;
    }
}

